use std::collections::HashSet;

fn main() {
    let input = "";

    let windows = [4, 14];
    let mut subslice: HashSet<char>;

    for (part, window) in windows.iter().enumerate() {
        for i in 0..(input.len() - window) {
            subslice = HashSet::from_iter(input.get(i..i + window).unwrap().chars());

            if subslice.len() == *window {
                println!("Part {}: {}", part + 1, i + window);
                break;
            }
        }
    }
} 
